import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import { useNavigate } from 'react-router-dom';

const slides = [
  {
    id: 1,
    image: "https://img.freepik.com/free-vector/flat-world-humanitarian-day-background_23-2149456286.jpg",
    title: "Give Medicine. Give Hope.",
    subtitle: "Your extra medicine can change a life today.",
  },
  {
    id: 2,
    image: "https://static.vecteezy.com/system/resources/thumbnails/008/575/634/small/donation-boxes-background-free-vector.jpg",
    title: "Support Our Frontliners",
    subtitle: "Donate supplies and fuel the fight for health.",
  },
  {
    id: 3,
    image: "data:image/jpeg;base64,",
    title: "Be Someone’s Reason to Heal",
    subtitle: "A small donation. A big difference.",
  },
  {
    id: 4,
    image: "data:image/jpeg;base64,",
    title: "Help. Heal. Repeat.",
    subtitle: "Join the movement to support better healthcare.",
  }
];

const MainCrousal = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  let navg = useNavigate(); // Initialize useNavigate hook
    
      // Function to handle navigation for buttons
      const doNavigate = (url) => {
        navg("/" + url);
      };

  useEffect(() => {
    if (!isAutoPlaying) return;
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [currentSlide, isAutoPlaying]);

  return (
    <div
      className="relative w-full max-w-7xl mx-auto h-96 md:h-[500px] overflow-hidden rounded-xl shadow-lg "
      onMouseEnter={() => setIsAutoPlaying(false)}
      onMouseLeave={() => setIsAutoPlaying(true)}
    >
      {/* Slide container */}
      <div
        className="flex transition-transform duration-700 ease-in-out h-full"
        style={{ transform: `translateX(-${currentSlide * 100}%)` }}
      >
        {slides.map((slide) => (
          <div key={slide.id} className="relative w-full h-full flex-shrink-0">
            {/* Image Layer */}
            <img
              src={slide.image}
              alt={slide.title}
              className="w-full h-full object-cover"
            />

            {/* Light Overlay */}
            <div className="absolute inset-0 bg-white/50 backdrop-blur-sm z-10" />

            {/* Text Content */}
            <div className="absolute inset-0 flex flex-col justify-center items-center text-center z-20 px-4">
              <h2 className="text-3xl md:text-5xl font-bold text-gray-800 mb-3">
                {slide.title}
              </h2>
              <p className="text-lg md:text-xl text-gray-700 font-medium">
                {slide.subtitle}
              </p>
              <button className="mt-6 px-6 py-2 bg-white border border-gray-300 text-gray-800 rounded-full hover:bg-gray-100 transition duration-300 shadow-md" onClick={() => doNavigate('signup')}>
                SignUp Now
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Arrows */}
      <button
        onClick={() =>
          setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length)
        }
        className="absolute left-4 top-1/2 -translate-y-1/2 z-30 bg-white/80 hover:bg-white text-gray-800 p-2 rounded-full shadow"
      >
        <ChevronLeft className="w-6 h-6" />
      </button>
      <button
        onClick={() =>
          setCurrentSlide((prev) => (prev + 1) % slides.length)
        }
        className="absolute right-4 top-1/2 -translate-y-1/2 z-30 bg-white/80 hover:bg-white text-gray-800 p-2 rounded-full shadow"
      >
        <ChevronRight className="w-6 h-6" />
      </button>

      {/* Dots */}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-3 z-30">
        {slides.map((_, idx) => (
          <button
            key={idx}
            onClick={() => setCurrentSlide(idx)}
            className={`w-3 h-3 rounded-full transition ${
              idx === currentSlide ? 'bg-gray-800' : 'bg-gray-400'
            }`}
          />
        ))}
      </div>
    </div>
  );
};

export default MainCrousal;
